mapper.select:
SELECT * FROM ( SELECT t.*, ROWNUM AS rn FROM (SELECT * FROM users u JOIN (SELECT * FROM user_info i JOIN departments d ON i.dept_no = d.dept_no) a ON u.emp_no = a.emp_no
ORDER BY u.emp_no DESC ) t )
WHERE rn >= (#{cri.page}-1) * #{cri.perPageNum} +1 AND rn <= #{cri.page} * #{cri.perPageNum}

mapper.update:
update user_info
set rank = #{rank},
	dept_no = #{deptNo}
where emp_no = #{empNo}

update users
set emp_status = #{empStatus},
	emp_grade = #{empGrade}
where emp_no = #{empNo}

mapper.select:
select count(*)
from user_info
where pno = #{pno}

mapper.update:
update user_info
set name = #{name},
	rank = #{rank},
	dept_no = #{deptNo},
	pno = #{pno}
where emp_no = #{empNo}
