mapper.insert:
INSERT INTO BOARD (
			ID
		  , BOARD_NO
		  , BOARD_TYPE
		  , TITLE
		  , CONTENT
		  , INSERT_DATE

) VALUES (
			#{id}
		  , BOARD_SEQ.NEXTVAL
		  , #{boardType}
		  , #{title}
		  , #{content}
		  , sysdate

)

INSERT INTO FILES 
				   ( FILE_NO
				   , ORIGIN_FILE_NAME
				   , CHANGE_FILE_NAME
				   , FILE_SIZE
				   , FILE_DATE
				   , BOARD_NO
				   )
				   VALUES 
				   ( #{fileNo}
				   , #{originFileName}
				   , #{changeFileName}
				   , #{fileSize}
				   , sysdate
				   , #{boardNo}
				   )

mapper.update:
UPDATE BOARD


		TITLE = #{title},


		content = #{content},

	UPDATE_DATE = #{updateDate}

WHERE BOARD_NO = #{boardNo}
