mapper.select:
SELECT * FROM ( SELECT t.*, ROWNUM AS rn FROM (select c.id, c.dept_auth, c.emp_no, c.name, b.board_no, b.board_type, b.title, b.content, b.insert_date, b.update_date, count, bt_no from board b join (select u.id, a.dept_auth, a.emp_no, a.name from users u join (select d.dept_auth, i.emp_no, i.name from departments d join user_info i on d.dept_no = i.dept_no) a on u.emp_no = a.emp_no) c on b.id = c.id
ORDER BY b.board_no DESC ) t)
WHERE rn >= (#{cri.page}-1) * #{cri.perPageNum} +1 AND rn <= #{cri.page} * #{cri.perPageNum}

mapper.insert:

   INSERT INTO BOARD (
   			ID
   		  , BOARD_NO
   		  , BOARD_TYPE
   		  , TITLE
   		  , CONTENT
   		  , INSERT_DATE
   		  , BT_NO

   ) VALUES (
   			#{id}
   		  , #{boardNo}
   		  , #{boardType}
   		  , #{title}
   		  , #{content}
   		  , sysdate
   		  , (	SELECT NVL(MAX(bt_no),0) +1
				FROM   BOARD
				WHERE BOARD_TYPE = #{boardType})

   )

mapper.update:
UPDATE BOARD


		TITLE = #{title},


		content = #{content},

	UPDATE_DATE = #{updateDate}

WHERE BOARD_NO = #{boardNo}

UPDATE com
SET com_content = #{comContent}
WHERE com_no = #{comNo}
