mapper.select:
SELECT * FROM ( SELECT t.*, ROWNUM AS rn FROM ( select pl.oper_date , pl.destination , pl.befored , pl.afterd , pl.imprest , pl.purpose , p.pay_status1 , p.pay_status2 , p.pay_status3 , p.approver3 , p.id , ui.name , u.emp_no , d.dept_name , p.pay_no , p.doc_type , p.issue_date from payment p , payment_logbook pl , users u , user_info ui , departments d where p.pay_no = pl.pay_no and p.id = u.id and u.emp_no = ui.emp_no and d.dept_no = ui.dept_no and p.id = #{id}
AND to_char(p.issue_date, 'YYYY-MM') = #{monthDate}
order by pl.oper_date desc ) t )
WHERE rn >= (#{cri.page}-1) * 10 +1 and rn <= #{cri.page} * 10

select count(*)
from car
where car_no = #{carNo}
