mapper.insert:

		    SELECT ROOM_SEQ.NEXTVAL FROM DUAL

  		INSERT INTO ROOM (
  						  ROOM_NO
  						 ,ROOM_NAME
  						 ,ROOM_TIME

  						 )
  				  VALUES (
  				  		  #{roomNo}
  				  		 ,#{roomName}
  				  		 ,sysdate

  				  		 )

mapper.delete:
DELETE FROM ROOM_mem
WHERE ROOM_NO = #{roomNo}
AND id = #{id}

mapper.insert:

		    SELECT MESSAGE_SEQ.NEXTVAL FROM DUAL

  		INSERT INTO MESSAGE (
  						  ROOM_NO
  						 ,ID
  						 ,CONTENT
  						 ,MSG_TIME
  						 ,MSG_NO

  						 )
  				  VALUES (
  				  		  #{roomNo}
  				  		 ,#{id}
  				  		 ,#{content}
  				  		 ,sysdate
  				  		 ,#{msgNo}
  				  		 )

  		INSERT INTO ROOM_MEM 
					  		(
					  		MEM_TIME      
							,ID                
							,ROOM_NO
				  			)
  					VALUES (
  							sysdate
  							,#{id}
  							,#{roomNo}
							)

mapper.select:
    SELECT i.emp_no, u.id, i.name, i.rank, d.dept_name
	FROM user_info i
	JOIN departments d ON i.dept_no = d.dept_no
	JOIN users u ON i.emp_no = u.emp_no
	WHERE u.id != #{id}
	AND u.id NOT IN (
	    SELECT rm.id 
	    FROM room_mem rm
	    WHERE rm.room_no = #{roomNo}
	)
