mapper.insert:

		    SELECT ROOM_SEQ.NEXTVAL FROM DUAL

  		INSERT INTO ROOM (
  						  ROOM_NO
  						 ,ROOM_NAME
  						 ,ROOM_TIME
  						 ,ID
  						 )
  				  VALUES (
  				  		  #{roomNo}
  				  		 ,#{roomName}
  				  		 ,sysdate
  				  		 ,#{id}
  				  		 )


		    SELECT MESSAGE_SEQ.NEXTVAL FROM DUAL

  		INSERT INTO MESSAGE (
  						  ROOM_NO
  						 ,ID
  						 ,CONTENT
  						 ,MSG_TIME
  						 ,MSG_NO

  						 )
  				  VALUES (
  				  		  #{roomNo}
  				  		 ,#{id}
  				  		 ,#{content}
  				  		 ,sysdate
  				  		 ,#{msgNo}
  				  		 )
