mapper.update:
update commute 
set over_work_time = NVL(to_number(trunc((over_work_end - over_work_start)*24*60)) ,0)
	where id = #{id} 
	and to_char(day,'yyyy-MM-DD') = #{day2}

mapper.select:

select * from commute
where id = #{id}
and to_char(day,'YY/MM/DD') = to_char(sysdate,'YY/MM/DD')

select u.emp_no, d.dept_name, ui.name, cm.work_start, cm.work_end, cm.day, cm.off_status, NVL(to_number(trunc((work_end - work_start)*24*60)) ,0)as work_time, NVL(cm.over_work_time,0) as over_work_time, o.off_count from users u, departments d, user_info ui, commute cm, off o where d.dept_no = ui.dept_no and ui.emp_no = u.emp_no and u.id = cm.id and cm.id = o.id and u.id = #{id}
AND to_char(cm.day, 'YYYY-MM') = #{monthDate}
order by cm.day desc

		SELECT NVL(COUNT(commute.day), 0) AS count, NVL(SUM(commute.over_work_time), 0) AS over
			FROM (
  				SELECT day, over_work_time
  				FROM commute
  				WHERE id = #{id}
    			AND work_end IS NOT NULL

       	 	and TO_CHAR(day,'yyyy-MM') = TO_CHAR(sysdate, 'yyyy-MM') 


       	 	and TO_CHAR(day,'yyyy-MM') = #{monthDate}

			) commute

        SELECT count(day) AS count
  		FROM commute
	  	WHERE off_status = 'N'
	    and TO_CHAR(day,'yyyy-MM') = #{monthDate}
	    AND id = #{id}
