mapper.select:
SELECT m.pro_color, m.pro_star_check, m.home_tab, p.pro_name, p.pro_content, p.pro_no
FROM pro_mem m JOIN pro p
ON m.pro_no = p.pro_no
WHERE p.pro_no = #{proNo}
AND m.id = #{id}

{call cre_pro_writing(
		#{proNo, 		   mode=IN,  jdbcType=INTEGER,  javaType=INTEGER},
		#{id, 	           mode=IN,  jdbcType=VARCHAR,  javaType=String},
		#{postTitle,	   mode=IN,  jdbcType=VARCHAR,  javaType=String},
		#{writingContent,  mode=IN,  jdbcType=VARCHAR,  javaType=String}
	)
}

mapper.insert:
{call cre_pro_work( #{proNo, mode=IN, jdbcType=INTEGER}, #{id, mode=IN, jdbcType=VARCHAR}, #{postTitle, mode=IN, jdbcType=VARCHAR}, #{workGroupNo, mode=IN, jdbcType=INTEGER}, #{workStatus, mode=IN, jdbcType=CHAR}, #{workPri, mode=IN, jdbcType=CHAR}, #{workStartDay, mode=IN, jdbcType=DATE}, #{workStartTime, mode=IN, jdbcType=DATE}, #{workEndDay, mode=IN, jdbcType=DATE}, #{workEndTime, mode=IN, jdbcType=DATE}, #{workContent, mode=IN, jdbcType=VARCHAR}
, #{workPersonArr, mode=IN, jdbcType=ARRAY, javaType=java.util.List, typeHandler=com.groo.bear.pro.mapper.StringListTypeHandler}
, null
) }
