mapper.update:
UPDATE sch_parti
SET parti_check = #{partiCheck}
WHERE sch_no = #{schNo}
AND id = #{id}

mapper.select:
SELECT '[' || CASE WHEN b.work_status = '1' THEN '요청' WHEN b.work_status = '2' THEN '진행' WHEN b.work_status = '3' THEN '피드백' WHEN b.work_status = '4' THEN '완료' WHEN b.work_status = '5' THEN '보류' END || '] ' || a.post_title AS title,
b.work_start_time AS "start", b.work_end_time AS end
FROM pro_post a JOIN pro_work b ON(a.pro_post_no = b.pro_post_no) WHERE a.pro_no = #{proNo}
UNION ALL
SELECT '일정 : ' || a.post_title AS title, b.sch_start_time AS "start", b.sch_end_time AS end FROM pro_post a JOIN pro_sch b ON(a.pro_post_no = b.pro_post_no) WHERE (b.personal_sch IS NULL OR b.personal_sch = 'N') AND a.pro_no = #{proNo}
